mapper.update:
update sys_role
set available = #{available,jdbcType=BIT},
  description = #{description,jdbcType=VARCHAR},
  `role` = #{role,jdbcType=VARCHAR}
where role_id = #{roleId,jdbcType=VARCHAR}

mapper.select:
select role_id, available, description, `role`
from sys_role
where role_id = #{roleId,jdbcType=VARCHAR}
